package be.planetsizebrain.advent.day2;

import java.util.ArrayList;
import java.util.List;

public class Exercise1 {

	private static List<String> LINES = new ArrayList<>();

	static {
		LINES.add("");
		LINES.add("");
		LINES.add("");
		LINES.add("");
		LINES.add("");
	}

	public static void main(String[] args) {
		StringBuilder code = new StringBuilder();
		int currentNumber = 5;
		for (String line : LINES) {
			for (char command : line.toCharArray()) {
				currentNumber = getNext(currentNumber, command);
			}

			code.append(currentNumber);
		}

		System.out.println(code.toString());
	}

	private static int getNext(int current, char command) {
		switch (command) {
			case 'U': return current < 4 ? current : current - 3;
			case 'D': return current > 6 ? current : current + 3;
			case 'L': return (current != 1 && current != 4 && current != 7) ? current - 1 : current;
			case 'R': return current % 3 == 0 ? current : current + 1;
		}

		return 0;
	}
}