package be.planetsizebrain.advent.day2;

import java.util.ArrayList;
import java.util.List;

public class Exercise2 {

	private static List<String> LINES = new ArrayList<>();

	static {
		LINES.add("");
		LINES.add("");
		LINES.add("");
		LINES.add("");
		LINES.add("");
	}

	public static void main(String[] args) {
		StringBuilder code = new StringBuilder();
		String currentCode = "5";
		for (String line : LINES) {
			for (char command : line.toCharArray()) {
				currentCode = getNext(currentCode, command);
			}

			code.append(currentCode);
		}

		System.out.println(code.toString());
	}

	private static String getNext(String current, char command) {
		switch (current) {
			case "1": return command == 'D' ? "3" : current;
			case "2": return command == 'R' ? "3" : (command == 'D' ? "6" : current);
			case "3": return command == 'U' ? "1" : (command == 'D' ? "7" : (command == 'L' ? "2" : "4"));
			case "4": return command == 'L' ? "3" : (command == 'D' ? "8" : current);
			case "5": return command == 'R' ? "6" : current;
			case "6": return command == 'U' ? "2" : (command == 'D' ? "A" : (command == 'L' ? "5" : "7"));
			case "7": return command == 'U' ? "3" : (command == 'D' ? "B" : (command == 'L' ? "6" : "8"));
			case "8": return command == 'U' ? "4" : (command == 'D' ? "C" : (command == 'L' ? "7" : "9"));
			case "9": return command == 'L' ? "8" : current;
			case "A": return command == 'U' ? "6" : (command == 'R' ? "B" : current);
			case "B": return command == 'U' ? "7" : (command == 'D' ? "D" : (command == 'L' ? "A" : "C"));
			case "C": return command == 'U' ? "8" : (command == 'L' ? "B" : current);
			case "D": return command == 'U' ? "B" : current;
		}

		return "";
	}
}